mapper.select:
SELECT * FROM ( SELECT PB.BUY_ID AS BUYID, PB.PRODUCT_ID AS PRODUCTID, PB.MEMBER_ID AS MEMBERID, PB.BUY_QTY AS BUYQTY, PB.BUY_UNIT_PRICE AS BUYUNITPRICE, PB.BUY_TOTAL_PRICE AS BUYTOTALPRICE, PB.BUY_DELIVERY_DATE AS BUYDELIVERYDATE, PB.CREATED_DATE AS CREATEDDATE, P.PRODUCT_NAME AS PRODUCTNAME, P.PRODUCT_DESCRIPTION AS PRODUCTDESCRIPTION, P.PRODUCT_UNIT_PRICE AS PRODUCTUNITPRICE, P.PRODUCT_MIN_QTR AS PRODUCTMINQTR, P.SELL_MEMBER_ID AS SELLMEMBERID, I.IMAGE_NAME AS IMAGENAME, 'buy' AS SOURCE FROM TB_PRODUCT_BUY PB LEFT JOIN TB_PRODUCT P ON PB.PRODUCT_ID = P.PRODUCT_ID LEFT JOIN TB_IMAGES I ON P.PRODUCT_ID = I.PRODUCT_ID AND I.IMAGE_TYPE = '200' WHERE PB.MEMBER_ID = #{memberId}
UNION ALL
SELECT R.REV_ID AS BUYID, R.PRODUCT_ID AS PRODUCTID, R.MEMBER_ID AS MEMBERID, R.REV_QTY AS BUYQTY, R.REV_UNIT_PRICE AS BUYUNITPRICE, R.REV_TOTAL_PRICE AS BUYTOTALPRICE, R.REV_DELIVERY_DATE AS BUYDELIVERYDATE, R.CREATED_DATE AS CREATEDDATE, P.PRODUCT_NAME AS PRODUCTNAME, P.PRODUCT_DESCRIPTION AS PRODUCTDESCRIPTION, P.PRODUCT_UNIT_PRICE AS PRODUCTUNITPRICE, P.PRODUCT_MIN_QTR AS PRODUCTMINQTR, P.SELL_MEMBER_ID AS SELLMEMBERID, I.IMAGE_NAME AS IMAGENAME, 'reserve' AS SOURCE FROM TB_PRODUCT_RESERVATION R LEFT JOIN TB_PRODUCT P ON R.PRODUCT_ID = P.PRODUCT_ID LEFT JOIN TB_IMAGES I ON P.PRODUCT_ID = I.PRODUCT_ID AND I.IMAGE_TYPE = '200' WHERE R.MEMBER_ID = #{memberId} ) ALL_PURCHASES ORDER BY ALL_PURCHASES.CREATEDDATE DESC

SELECT P.PRODUCT_ID, P.PRODUCT_NAME, P.PRODUCT_UNIT_PRICE, TO_CHAR(P.CREATED_DATE, 'YYYY-MM-DD') AS CREATED_DATE, SELLER.MEMBER_BNAME AS SELLER_NAME, BUYER.MEMBER_NAME AS BUYER_NAME, BUYER.MEMBER_ADDR, BUYER.MEMBER_DETAIL_ADDR, TO_CHAR(B.BUY_DELIVERY_DATE, 'YYYY-MM-DD') AS DELIVERY_DATE, '즉시구매' AS PURCHASE_TYPE, BUYER.MEMBER_TEL AS BUYER_TEL, TO_CHAR(B.CREATED_DATE, 'YYYY-MM-DD HH24:MI') AS BUY_DATE, B.BUY_QTY, CD_PRODUCT.LOW_CODE_NAME AS PRODUCT_CODE_NAME, B.BUY_ID FROM TB_PRODUCT_BUY B JOIN TB_PRODUCT P ON B.PRODUCT_ID = P.PRODUCT_ID JOIN TB_MEMBER SELLER ON P.SELL_MEMBER_ID = SELLER.MEMBER_ID JOIN TB_MEMBER BUYER ON B.MEMBER_ID = BUYER.MEMBER_ID LEFT JOIN TB_CODE_DETAIL CD_PRODUCT ON P.PRODUCT_CODE = CD_PRODUCT.DETAIL_CODE_ID WHERE SELLER.MEMBER_ID = #{memberId}
UNION ALL
SELECT P.PRODUCT_ID, P.PRODUCT_NAME, P.PRODUCT_UNIT_PRICE, TO_CHAR(P.CREATED_DATE, 'YYYY-MM-DD') AS CREATED_DATE, SELLER.MEMBER_BNAME AS SELLER_NAME, BUYER.MEMBER_NAME AS BUYER_NAME, BUYER.MEMBER_ADDR, BUYER.MEMBER_DETAIL_ADDR, TO_CHAR(R.REV_DELIVERY_DATE, 'YYYY-MM-DD') AS DELIVERY_DATE, '예약구매' AS PURCHASE_TYPE, BUYER.MEMBER_TEL AS BUYER_TEL, TO_CHAR(R.CREATED_DATE, 'YYYY-MM-DD HH24:MI') AS BUY_DATE, R.REV_QTY, CD_PRODUCT.LOW_CODE_NAME AS PRODUCT_CODE_NAME, R.REV_ID FROM TB_PRODUCT_RESERVATION R JOIN TB_PRODUCT P ON R.PRODUCT_ID = P.PRODUCT_ID JOIN TB_MEMBER SELLER ON P.SELL_MEMBER_ID = SELLER.MEMBER_ID JOIN TB_MEMBER BUYER ON R.MEMBER_ID = BUYER.MEMBER_ID LEFT JOIN TB_CODE_DETAIL CD_PRODUCT ON P.PRODUCT_CODE = CD_PRODUCT.DETAIL_CODE_ID WHERE SELLER.MEMBER_ID = #{memberId} AND R.REV_STATUS = '002' -- 예약완료 상태 ORDER BY BUY_DATE DESC

mapper.update:
UPDATE TB_ALARM
SET IS_READ = 'Y'
WHERE ALARM_ID = #{alarmId}
